"use client"
import axios from "axios"

function TablaEstudiantes({ estudiantes, obtenerEstudiantes }) {
  function eliminarEstudiante(numero_control) {
    if (window.confirm("¿Estás seguro de eliminar este estudiante?")) {
      try {
        axios
          .delete(`http://localhost:5000/api/estudiantes/${numero_control}`)
          .then(() => {
            alert("Estudiante eliminado correctamente")
            if (typeof obtenerEstudiantes === "function") {
              obtenerEstudiantes() // Refresca la lista
            }
          })
          .catch((error) => {
            alert("Error al eliminar el estudiante")
            console.error(error)
          })
      } catch (error) {
        alert("Error al eliminar el estudiante")
        console.error(error)
      }
    }
  }

  return (
    <div>
      <h2>Lista de Estudiantes</h2>
      <table className="table">
        <thead>
          <tr>
            <th>Número de Control</th>
            <th>Nombre</th>
            <th>Apellido</th>
            <th>Carrera</th>
            <th>Acciones</th>
          </tr>
        </thead>
        <tbody>
          {estudiantes.map((estudiante) => (
            <tr key={estudiante.numero_control}>
              <td>{estudiante.numero_control}</td>
              <td>{estudiante.nombre}</td>
              <td>{estudiante.apellido}</td>
              <td>{estudiante.carrera}</td>
              <td>
                <button className="btn btn-danger" onClick={() => eliminarEstudiante(estudiante.numero_control)}>
                  Eliminar
                </button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  )
}

export default TablaEstudiantes
