import TablaEstudiantes from "./TablaEstudiantes"
import FormularioEstudiante from "./FormularioEstudiante"
import TablaJustificantes from "./TablaJustificantes"
import { useEffect, useState } from "react"
import axios from "axios"

const VistaEnfermeria = () => {
  const [estudiantes, setEstudiantes] = useState([])
  const [estudianteEditar, setEstudianteEditar] = useState(null)
  const [justificantes, setJustificantes] = useState([])

  // Obtener estudiantes
  const obtenerEstudiantes = async () => {
    try {
      const res = await axios.get("http://localhost:5000/api/estudiantes")
      setEstudiantes(res.data)
    } catch (err) {
      console.error("Error al cargar estudiantes", err)
    }
  }

  // Obtener justificantes con estado "pendiente_enfermeria"
  const obtenerJustificantes = async () => {
    try {
      const res = await axios.get("http://localhost:5000/api/justificantes?estado=pendiente_enfermeria")
      setJustificantes(res.data)
    } catch (err) {
      console.error("Error al cargar justificantes", err)
    }
  }

  // Validar justificante (enviar a coordinación)
  const enviarJustificante = async (id) => {
    try {
      await axios.put(`http://localhost:5000/api/justificantes/${id}/enviar`)
      alert("Justificante enviado a Coordinación")
      obtenerJustificantes() // refresca la tabla
    } catch (error) {
      console.error("Error al enviar justificante:", error)
    }
  }

  useEffect(() => {
    obtenerEstudiantes()
    obtenerJustificantes()
  }, [])

  return (
    <div>
      <h2>Panel de Enfermería</h2>
      <FormularioEstudiante
        obtenerEstudiantes={obtenerEstudiantes}
        estudianteEditar={estudianteEditar}
      />
      <TablaEstudiantes
        estudiantes={estudiantes}
        onEditar={setEstudianteEditar}
        obtenerEstudiantes={obtenerEstudiantes}
      />
      <TablaJustificantes
        justificantes={justificantes}
        setJustificantes={setJustificantes}
      />

      {/* Tabla de justificantes obtenidos desde el backend */}
      <div style={{ marginTop: "2rem" }}>
        <h2>Lista de Justificantes</h2>
        {justificantes.length === 0 ? (
          <p>No hay justificantes pendientes.</p>
        ) : (
          <table border={1} cellPadding={8} style={{ width: "100%", borderCollapse: "collapse" }}>
            <thead>
              <tr>
                <th>ID</th>
                <th>No. Control</th>
                <th>Motivo</th>
                <th>Fecha</th>
                <th>Estado</th>
                <th>Acciones</th>
              </tr>
            </thead>
            <tbody>
              {justificantes.map(j => (
                <tr key={j.id}>
                  <td>{j.id}</td>
                  <td>{j.numero_control}</td>
                  <td>{j.motivo}</td>
                  <td>{j.fecha}</td>
                  <td>{j.estado}</td>
                  <td>
                    <button onClick={() => enviarJustificante(j.id)}>
                      Enviar a Coordinación
                    </button>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        )}
      </div>
    </div>
  )
}

export default VistaEnfermeria
