"use client"

import { useEffect, useState } from "react"
import TablaEstudiantes from "./TablaEstudiantes"
import FormularioEstudiante from "./FormularioEstudiante"
import TablaJustificantes from "./TablaJustificantes"

const VistaEnfermeria = ({
  justificantes = [],
  setJustificantes = () => {},
  estudiantes = [],
  obtenerEstudiantes = () => {},
}) => {
  const [estudianteEditar, setEstudianteEditar] = useState(null)
  const [mensaje, setMensaje] = useState(null)

  useEffect(() => {
    obtenerEstudiantes()
  }, [])

  // Función para enviar justificante a coordinación
  const enviarACoordinacion = (id) => {
    console.log("Enviando justificante a coordinación:", id)

    const nuevosJustificantes = justificantes.map((justificante) => {
      if (justificante.id === id && justificante.estado === "pendiente_enfermeria") {
        return { ...justificante, estado: "pendiente_coordinacion" }
      }
      return justificante
    })

    console.log("Nuevos justificantes:", nuevosJustificantes)
    setJustificantes(nuevosJustificantes)

    // Mostrar mensaje de éxito
    setMensaje({
      tipo: "success",
      texto: "✅ Justificante enviado correctamente a Coordinación",
    })

    // Ocultar mensaje después de 3 segundos
    setTimeout(() => {
      setMensaje(null)
    }, 3000)
  }

  return (
    <div>
      <div
        style={{
          backgroundColor: "#f0f9ff",
          padding: "1.5rem",
          borderRadius: "0.375rem",
          marginBottom: "2rem",
          border: "1px solid #bae6fd",
        }}
      >
        <h2 style={{ color: "#075985", marginBottom: "0.5rem" }}>Panel de Enfermería</h2>
        <p style={{ color: "#0369a1" }}>
          Bienvenido al panel de enfermería. Aquí puedes gestionar estudiantes y justificantes médicos.
        </p>
      </div>

      {/* Mensaje de éxito */}
      {mensaje && (
        <div
          style={{
            backgroundColor: mensaje.tipo === "success" ? "#dcfce7" : "#fee2e2",
            color: mensaje.tipo === "success" ? "#16a34a" : "#dc2626",
            padding: "12px",
            borderRadius: "6px",
            marginBottom: "16px",
            border: `1px solid ${mensaje.tipo === "success" ? "#86efac" : "#fca5a5"}`,
            textAlign: "center",
            fontWeight: "500",
          }}
        >
          {mensaje.texto}
        </div>
      )}

      <div style={{ display: "grid", gap: "2rem" }}>
        <div>
          <FormularioEstudiante obtenerEstudiantes={obtenerEstudiantes} estudianteEditar={estudianteEditar} />
        </div>

        <div>
          <TablaJustificantes
            justificantes={justificantes}
            setJustificantes={setJustificantes}
            onEnviarACoordinacion={enviarACoordinacion}
          />
        </div>
      </div>

      <div>
        <TablaEstudiantes
          estudiantes={estudiantes}
          onEditar={setEstudianteEditar}
          obtenerEstudiantes={obtenerEstudiantes}
        />
      </div>
    </div>
  )
}

export default VistaEnfermeria
