import React from 'react';
import axios from 'axios';

const TablaEstudiantes = ({ estudiantes, onEditar, obtenerEstudiantes }) => {
  // Función para eliminar estudiante
  const eliminarEstudiante = async (numero_control) => {
    try {
      await axios.delete(`http://localhost:5000/estudiantes/${numero_control}`);
      alert('Estudiante eliminado correctamente');
      obtenerEstudiantes(); // Refresca la lista de estudiantes
    } catch (error) {
      console.error('Error al eliminar estudiante:', error);
      alert('Ocurrió un error al eliminar el estudiante.');
    }
  };

  return (
    <table>
      <thead>
        <tr>
          <th>No. Control</th>
          <th>Nombre</th>
          <th>Carrera</th>
          <th>Semestre</th>
          <th>Correo</th>
          <th>Acciones</th>
        </tr>
      </thead>
      <tbody>
        {estudiantes.map(estudiante => (
          <tr key={estudiante.numero_control}>
            <td>{estudiante.numero_control}</td>
            <td>{estudiante.nombre_completo}</td>
            <td>{estudiante.carrera}</td>
            <td>{estudiante.semestre}</td>
            <td>{estudiante.correo}</td>
            <td>
              <button onClick={() => onEditar(estudiante)}>Editar</button>
              <button
                onClick={() =>
                  window.confirm("¿Seguro que deseas eliminar este estudiante?")
                    ? eliminarEstudiante(estudiante.numero_control)
                    : null
                }
                style={{ marginLeft: '8px', color: 'red' }}
              >
                Eliminar
              </button>
            </td>
          </tr>
        ))}
      </tbody>
    </table>
  );
};

export default TablaEstudiantes;
